// Este arquivo fornece acesso ao som de notificação via JavaScript
// Contém uma versão base64 de um som de notificação simples

const notificationSound = "data:audio/mp3;base64,";

// Exporta uma função que cria um elemento de áudio com o som
function createNotificationSound() {
  const audio = new Audio(notificationSound);
  audio.volume = 0.5;
  return audio;
}

// Exporta uma função que reproduz o som
function playNotificationSound() {
  const audio = createNotificationSound();
  return audio.play();
}

// Disponibiliza as funções no escopo global
window.createNotificationSound = createNotificationSound;
window.playNotificationSound = playNotificationSound;